mapper.select:
select
id,orderid,status,userid,pid,shumu,dianhua,dizhi,
beizhu,shijian

from t_dingdan


        and id = #{id}


        and orderid = #{orderid}


        and status = #{status}


        and userid = #{userid}


        and pid = #{pid}


        and shumu = #{shumu}


        and dianhua = #{dianhua}


        and dizhi = #{dizhi}


        and beizhu = #{beizhu}


        and shijian = #{shijian}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from t_dingdan


        and id = #{id}


        and orderid = #{orderid}


        and status = #{status}


        and userid = #{userid}


        and pid = #{pid}


        and shumu = #{shumu}


        and dianhua = #{dianhua}


        and dizhi = #{dizhi}


        and beizhu = #{beizhu}


        and shijian = #{shijian}
