mapper.select:
select
id,
u_id,
uc_name,
uc_address,
uc_tel,
uc_status


from t_user_contact


        and id = #{id}


        and u_id = #{uId}


        and uc_name = #{ucName}


        and uc_address = #{ucAddress}


        and uc_tel = #{ucTel}


        and uc_status = #{ucStatus}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from t_user_contact


        and id = #{id}


        and u_id = #{uId}


        and uc_name = #{ucName}


        and uc_address = #{ucAddress}


        and uc_tel = #{ucTel}


        and uc_status = #{ucStatus}
